<?php

use DrupalJedi\CssTreeShaking;
use PHPUnit\Framework\TestCase;

/**
 * Class ShackingTest.
 */
class ShackingTest extends TestCase {

  /**
   * Testing, that styles should be shaken based on styles size.
   *
   * @dataProvider getShouldBeShackenData
   * @covers \DrupalJedi\CssTreeShaking::shouldBeShacken
   *
   * @param string $source
   *   Source HTML.
   * @param number $limit
   *   Max size for styles.
   * @param bool $expected
   *   Expected test result for current source.
   */
  public function testShouldBeShacken(string $source, int $limit, bool $expected) {
    $shaker = new CssTreeShaking($source, $limit);

    $this->assertSame($expected, $shaker->shouldBeShacken());
  }

  /**
   * Testing, that styles can be extracted from html.
   *
   * @dataProvider getExtractStylesData
   * @covers \DrupalJedi\CssTreeShaking::extractStyles
   *
   * @param string $source
   *   Source HTML.
   * @param bool $expected
   *   Expected test result for current source.
   */
  public function testExtractStyles(string $source, bool $expected) {

    $shaker = new CssTreeShaking($source);
    $shaker->extractStyles();

    $styles = $shaker->getStyles();

    $this->assertSame($expected, (bool) $styles);
  }

  /**
   * Testing, that styles shaked successfuly.
   *
   * @dataProvider getShakeItData
   * @covers \DrupalJedi\CssTreeShaking::shakeIt
   *
   * @param string $source
   *   Source HTML.
   * @param string $expectedStyles
   *   Expected styles, that should be kept.
   * @param bool $force
   *   Use force mode or not.
   */
  public function testShakeIt(string $source, string $expectedStyles, bool $force) {
    $shaker = new CssTreeShaking($source);
    $shaker->shakeIt($force);
    $styles = '';

    foreach ($shaker->getStyles() as $style) {
      $styles .= $style->nodeValue;
    }

    $this->assertSame($expectedStyles, $styles);
  }

  /**
   * Testing, that HTML can be exported.
   *
   * @dataProvider getExportHtmlData
   * @covers \DrupalJedi\CssTreeShaking::exportHtml
   *
   * @param string $source
   *   Source HTML.
   * @param string $expectedHTML
   *   Expected styles, that should be kept.
   */
  public function testExportHtml(string $source, string $expectedHTML) {
    $shaker = new CssTreeShaking($source);

    $this->assertSame($expectedHTML, $shaker->exportHtml());
  }

  /**
   * Provide dummy html pages for testing.
   *
   * @return array
   *   Dummy HTML pages for shaking.
   */
  public function getShakeItData(): array {
    return [
      [
        '<html><head><style amp-custom>body{display:inline-block;}.missed-class{color:red;}</style></head><body></body></html>',
        'body{display:inline-block;}',
        TRUE,
      ],
      [
        '<html><head><style amp-custom></style></head><body></body></html>',
        '',
        TRUE,
      ],
      [
        '<html><head><style></style></head><body></body></html>',
        '',
        TRUE,
      ],
      [
        '<html><head></head><body></body></html>',
        '',
        TRUE,
      ],
      [
        '<html><head><style>body{display:inline-block;}</style><style>.missed-class{color:red;}</style></head><body></body></html>',
        'body{display:inline-block;}',
        TRUE,
      ],
      [
        '<html><head><style>.missed-class{color:red;}</style><style>body{display:inline-block;}</style></head><body></body></html>',
        '.missed-class{color:red;}body{display:inline-block;}',
        FALSE,
      ],
      [
        '<html><head><style amp-custom>body{display:inline-block;}.missed-class{color:red;}</style></head><body></body></html>',
