<?php

use DrupalJedi\CssTreeShaking;
use PHPUnit\Framework\TestCase;

/**
 * Class ShackingTest.
 */
class ShackingTest extends TestCase {

  /**
   * Testing, that styles should be shaken based on styles size.
   *
   * @dataProvider getShouldBeShackenData
   * @covers \DrupalJedi\CssTreeShaking::shouldBeShacken
   *
   * @param string $source
   *   Source HTML.
   * @param number $limit
   *   Max size for styles.
   * @param bool $expected
   *   Expected test result for current source.
   */
  public function testShouldBeShacken(string $source, int $limit, bool $expected): void {
    $shaker = new CssTreeShaking($source, $limit);

    $this->assertSame($expected, $shaker->shouldBeShacken());
  }

  /**
   * Testing, that styles can be extracted from html.
   *
   * @dataProvider getExtractStylesData
   * @covers \DrupalJedi\CssTreeShaking::extractStyles
   *
   * @param string $source
   *   Source HTML.
   * @param bool $expected
   *   Expected test result for current source.
   */
  public function testExtractStyles(string $source, bool $expected): void {

    $shaker = new CssTreeShaking($source);
    $shaker->extractStyles();

    $styles = $shaker->getStyles();

    $this->assertSame($expected, (bool) $styles);
  }

  /**
   * Provide dummy html pages for testing.
   *
   * @return array
   *   Dummy HTML pages for initialization.
   */
  public function getExtractStylesData(): array {
    return [
      [
        '<html><head></head><body></body></html>',
        FALSE
      ],
      [
        '<html><head><style amp-boilerplate>@-moz-keyframes -amp-start{from{visibility:hidden}to{visibility:visible}}</style></head><body></body></html>',
        FALSE
      ],
      [
        '<html><head><style></style></head><body></body></html>',
        FALSE
      ],
      [
        '<html><head><style amp-custom>body{display:inline-block;}.missed-not-so-class{color:red;}</style></head><body></body></html>',
        TRUE
      ],
    ];
  }

  /**
   * Provide dummy html pages for testing.
   *
   * @return array
   *   Dummy HTML pages for checking.
   */
  public function getShouldBeShackenData(): array {
    return [
      [
        '<html><head><style amp-custom>body{display:inline-block;}.missed-class{color:red;}</style></head><body></body></html>',
        50000,
        FALSE,
      ],
      [
        '<html><head><style amp-custom>body{display:inline-block;}.missed--class{color:red;}</style></head><body></body></html>',
        50000,
        TRUE,
      ],
      [
        '<html><head><style amp-custom>body{display:inline-block;}.missed-not-so-loooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooooong-class{color:red;}</style></head><body></body></html>',
        100,
        TRUE
      ],
    ];
  }

}
